mapper.select:
select article_id, user_id, subject, content, hit, created_at, updated_at
from article
where article_id = #{articleId}

mapper.update:
update article
set hit = hit + 1
where article_id = #{articleId}

mapper.select:
select comment_id, user_id, content, updated_at, parent_comment_id
from comment
where article_id = #{articleId}
