mapper.select:
select article_id, u.user_id, u.nickname as user_nickname, subject, content, hit, a.created_at, a.updated_at
from article a inner join user u on a.user_id = u.user_id
where article_id = #{articleId}

mapper.update:
update article
set hit = hit + 1
where article_id = #{articleId}

mapper.select:
select comment_id, u.user_id, u.nickname as user_nickname, content, c.updated_at, parent_comment_id
from comment c inner join user u on c.user_id = u.user_id
where article_id = #{articleId}
