mapper.select:
select plan_id, user_id, title
from plan
where (scope = 'PUBLIC'

    or user_id = #{userId}

)

    and title like concat('%',#{keyword},'%')

limit #{start}, #{SIZE}

select p.plan_id, p.user_id, p.title, p.order_string,
       pl.plan_list_id, pl.attraction_id
from plan p left join plan_list pl on (p.plan_id = pl.plan_id)
where p.plan_id = #{planId}

mapper.insert:
insert into plan_list(plan_id, attraction_id)
values(#{planId}, #{attractionId});

update plan
set order_string = concat(order_string, " ", (
    select plan_list_id
    from plan_list
    where plan_id = #{planId}
    order by plan_list_id desc
    limit 1
    ))
where plan_id = #{planId};

mapper.update:
update plan
set order_string = #{orderString}
where plan_id = #{planId}
