mapper.select:
select user_id, email_id, password, first_name, last_name, nickname,
       created_at, phone_number, status
from user where email_id = #{email}

mapper.update:
update user set email_id = #{emailId}, first_name = #{firstName},
                last_name = #{lastName}, nickname = #{nickname}, phone_number = #{phoneNumber}
where user_id = #{userId}
